			//封装获取元素对象的函数
			function ById(id){
				return document.getElementById(id);
			}
			/*获取元素对象(共六种):
			 1.document.getElementById("id名称")；
			 2.document.getElementsByClassName('class名')；
			 3.document.getElementsByTagName('标签名')；
			 4.document.getElementsByName("name名")；
			 5.document.querySelector('选择器')；
			 6.document.querySelectorAll('选择器')；
			*/
			var oBox = ById("box");
			var oMark_left = ById("mark_left");
			var oMark_right = ById("mark_right");
			var oBtn_left = ById("btn_left");
			var oBtn_right = ById("btn_right");
			var oTop = ById("top");
			var oBigul = ById("bigul");
            var oBigli = oBigul.getElementsByTagName("li");
            var oBottom = ById("bottom");
			var oSmallul = ById("smallul");
            var oSmallli = oSmallul.getElementsByTagName("li");
         //设置小图ul的宽度（单张小图的宽*所有小图的长度）
         oSmallul.style.width = oSmallli[0].offsetWidth * oSmallli.length + "px";
         //给左遮罩和左按钮添加移入事件
         oMark_left.onmouseenter = oBtn_left.onmouseenter = function(){
            	sport(oBtn_left,{opacity:100});
            }
         //给左遮罩和左按钮添加移出事件
             oMark_left.onmouseout = oBtn_left.onmouseout = function(){
            	sport(oBtn_left,{opacity:0});
            }
         //给右遮罩和右按钮添加移入事件
             oMark_right.onmouseenter = oBtn_right.onmouseenter = function(){
            	sport(oBtn_right,{opacity:100});
            }
         //给右遮罩和右按钮添加移入事件
             oMark_right.onmouseout = oBtn_right.onmouseout = function(){
            	sport(oBtn_right,{opacity:0});
            }
   			//声明一个控制当前图片的下标
			let indexA = 0;
            //声明一个zIndex的变量
		    let zIndex = 1;
		    //声明timer
		    let timer = null;
		    	solider()
		    	autoPlay()
		    //左按钮点击事件
         oBtn_left.onclick = function(){
	           indexA --;
	           	if(indexA ==-1){
           	 	indexA = oBigli.length-1; 
           	 }
	        	   	solider();
           }
         //右按钮点击事件
         oBtn_right.onclick = function(){
	           indexA++;
	           	if(indexA ==oBigli.length){
           	 	indexA =0;           	 
           	 }
	          	 	solider();
           }
         //给所有小图添加事件
         for(var i = 0,len = oSmallli.length;i<len;i++){
         	//记录当前下标	
         		oSmallli[i].index = i;
         	//移入事件
         	oSmallli[i].onmouseenter = function(){
         		sport(this,{opacity:100});
         	}
         	//移出事件
         	oSmallli[i].onmouseleave = function(){
         		if(this.index != indexA){
         		sport(this,{opacity:30});
         	}        	
        }
         	//点击事件
        oSmallli[i].onclick = function(){
        	indexA = this.index;
        	   solider();
        }
      }
         //图片轮播：
         function solider(){
         	//大图轮播（改变z-index的值，因为设置了定位，所以图片脱离文档流，叠加在一起，通过z-index的值[数值大的在上面，数值小的在下面]，实现大图的轮播）
         	oBigli[indexA].style.zIndex = ++ zIndex;
         	//小图轮播
         	
         	if(indexA === 0){
         		sport(oSmallul,{left:0});
         	}else if(indexA === oSmallli.length-1){
         		sport(oSmallul,{left:-(oSmallli.length-3)*oSmallli[0].offsetWidth});
         	
         	}else{
         		sport(oSmallul,{left:-(indexA-1)*oSmallli[0].offsetWidth});
         	
         	}
        //初始化小图透明
           for(let i = 0,len = oSmallli.length;i<len;i++){
           	sport(oSmallli[i],{opacity:30});
           }
           sport(oSmallli[indexA],{opacity : 100});
         }
//          //自动轮播
         function autoPlay(){
         	
         	timer = setInterval(function(){
         	indexA++;
         	if(indexA == oBigli.length){
         		indexA = 0;
         	}
         	solider()
         },2000)
        }
         //23. 给大盒子添加移入移出事件
				oBox.onmouseenter = function(){
					clearInterval(timer);
				}
				oBox.onmouseleave = function(){
					autoPlay();
				}


