window.onload = function(){
    var deff = $.ajax({
            type:"get",
            url:"../json/list.json",
            async:true
        })  
     deff.done( function(data){
       // console.log(data);
        var listCon = "";
        var produceCon = "";
        for(var attr in data){
           // console.log(data[attr]);
           listCon += `<li>${data[attr].name}</li>`;
           for(var i in data[attr].list ){
              // console.log(data[attr].list[i]);
              var pro = data[attr].list[i];
              produceCon += `<div id = "co1-con">
              <img src="${pro.src}" alt=""> 
             <p>${pro.name}</p>
             <p>${pro.describe}</p>
             <p><i class="iconfont5">&#xe6a3;</i>${pro.price}</p> 
         </div>
 `;
           }
        }
      //  $('#co1').html(produceCon);
        $('#listUl').html(listCon);
        $('#co1').click(function(){
           // alert('nihao')
           location.href = "../html/details.html";
        })
    })
}

$('.iconfont1').click(function(){
   location.href = "../html/index.html";
})
